  { backgroundImage: 'url(https://images.unsplash.com/photo-1634827485474-f8a4c4e87a4a)' },
  { backgroundImage: 'url(https://images.unsplash.com/photo-1632388225181-1688ba1d6b02)' },
  { backgroundImage: 'url(https://images.unsplash.com/photo-1634827029828-08686e8c3bb5)' },
];

const About = () => {
  return (
    <div className="about-page">
      <Container fluid>
        <Row>
          <Col sm={12} md={6} className="left-section">
            <div className="left-content">
              <h2>About Us</h2>
