import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import './About.css';
import img1 from '../images/Milestones.jpg';
import img2 from '../images/Neighbours.jpg';
import img3 from './img3.jpg';

const slideImages = [img1, img2, img3];

const About = () => {
  return (
    <div className="about">
      <Container>
        <Row>
          <Col lg={6}>
            <div className="about-img"></div>
          </Col>
          <Col lg={6}>
            <div className="about-content">
              <Slide easing="ease">
                <div className="each-slide">
                  <div className="slide-img" style={{ backgroundImage: `url(${slideImages[0]})` }}></div>
                  <h2>Our History</h2>
                  <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed a nulla at libero mollis bibendum. Aliquam erat volutpat.</p>
                </div>
                <div className="each-slide">
                  <div className="slide-img" style={{ backgroundImage: `url(${slideImages[1]})` }}></div>
                  <h2>Our Mission</h2>
                  <p>Curabitur sit amet justo et enim blandit tincidunt vel vel nisl. Donec suscipit magna sed metus suscipit.</p>
                </div>
                <div className="each-slide">
                  <div className="slide-img" style={{ backgroundImage: `url(${slideImages[2]})` }}></div>
                  <h2>Our Team</h2>
                  <p>Morbi rhoncus dolor risus, id pulvinar eros faucibus id. Sed tempor dignissim neque in varius.</p>
                </div>
              </Slide>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default About;
