import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import { Slide } from 'react-slideshow-image';
import 'react-slideshow-image/dist/styles.css';
import image1 from './images/Milestones.jpg';
import image2 from './images/W.jpg';
import image3 from './images/image3.jpg';

const About = () => {
  const slideImages = [image1, image2, image3];
  const properties = {
    duration: 5000,
    transitionDuration: 500,
    infinite: true,
    indicators: true,
    arrows: true,
  };

  return (
    <div>
      <Container fluid>
        <Row>
          <Col xs={12} md={6} className="about-left">
            <div className="about-image-container">
              <img
                src="./images/about-image.jpg"
                alt="about us"
                className="about-image"
              />
            </div>
          </Col>
          <Col xs={12} md={6} className="about-right">
            <div className="slide-container">
              <Slide {...properties}>
                <div className="each-slide" style={{backgroundImage: `url(${slideImages[0]})`}}>
                  <div>
                    <h2>Slide 1</h2>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                  </div>
                </div>
                <div className="each-slide" style={{backgroundImage: `url(${slideImages[1]})`}}>
                  <div>
                    <h2>Slide 2</h2>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                  </div>
                </div>
                <div className="each-slide" style={{backgroundImage: `url(${slideImages[2]})`}}>
                  <div>
                    <h2>Slide 3</h2>
                    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
                  </div>
                </div>
              </Slide>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default About;
